let clicked;
$(document).ready(function() {
    clicked = !0;
    $(".mode").click(function() {
        if (clicked) {
            $('head').append(`<link href="" id="dark" type="text/css" rel="stylesheet">`);
            clicked = !1
        } else {
            $('#dark').remove();
            clicked = !0
        }
    })
});
$(function() {
    $('[data-chat="open"]').on('click', function() {
        $('.chat').toggleClass('open')
    })
})
$(function() {
    $('[data-utility="open"]').on('click', function() {
        $('.utility').toggleClass('open')
    })
})
$(document).ready(function() {
    var valueOnLoad = "direct";
    $(".filter").not('.' + valueOnLoad).hide('3000');
    $(".filter").not('.' + valueOnLoad).hide('3000');
    $(".filter-btn").click(function() {
        var value = $(this).attr('data-filter');
        $(".filter").not('.' + value).hide('3000')
        $('.filter').filter('.' + value).show('3000')
    })
});